import React, { Component } from 'react'

class Form extends Component {
  state = {
    // datecomplete: this.Today(),
    // month: this.Month(),
    // week: this.Week(),
    // theme: null,
    // ticketnumber: null,
    // website: null,
    // remarks: null,
    // status: null,
    // skill : null
    start: null,
    end: null
  }
  addZeroinhours(i) {
    if (i < 10 && i !== 0) {
      i = "0" + i;
    } else if (i === 0) {
      i = 12;
    }
    return i;
  }
  addZeroinminutes(i) {
    if (i < 10) {
      i = "0" + i;
    }
    return i;
  }
  startTime = () => {
    let date = new Date(),
    hour = this.addZeroinhours(date.getHours()),
    minute = this.addZeroinminutes(date.getMinutes());
    this.setState({
      start: hour + ':' + minute
    })
  }
  endTime = () => {
    let date = new Date(),
    hour = this.addZeroinhours(date.getHours()),
    minute = this.addZeroinminutes(date.getMinutes());
    this.setState({
      end: hour + ':' + minute
    })
  }
  Today() {
    let date = new Date().getDate(),
    month = new Date().getMonth() + 1,
    year = new Date().getFullYear();

    return(date + '/' + month + '/' + year);
  }
  Week() {
    // const weekNumber = new Date().getDate();
    // const weekNumberofMonth = ['first', 'second', 'third', 'fourth', 'fifth'];
    // console.log(weekNumber);
    // console.log(weekNumberofMonth[Math.floor(31 / 7)]);
    // const today = new Date();
    // const firstDayOfYear = new Date(today.getFullYear(), 0, 1);
    // const pastDaysOfYear = (today - firstDayOfYear) / 86400000;
    // console.log(today, firstDayOfYear, pastDaysOfYear);
    // return Math.ceil((pastDaysOfYear + firstDayOfYear.getDay() + 1) / 7);
    var date = new Date();
    var days = ['Sunday','Monday','Tuesday','Wednesday',
                'Thursday','Friday','Saturday'],
        prefixes = ['First', 'Second', 'Third', 'Fourth', 'Fifth'];
        
    return prefixes[Math.floor(date.getDate() / 7)] + ' ' + days[date.getDay()];
  }
  
  Month() {
    const monthNames = ["January", "February", "March", "April", "May", "June",
  "July", "August", "September", "October", "November", "December"
  ];
    let month = new Date().getMonth();
    return(monthNames[month]);
  }
  
  handleChange = (e) => {
    this.setState({
      datecomplete: this.Today(),
      month: this.Month(),
      week: this.Week(),
      [e.target.id]: e.target.value
    })
  }
  handleSubmit = (e) => {
    e.preventDefault();
    this.props.addTicket(this.state);
    this.setState({
      theme: '',
      ticketnumber: '',
      website: '',
      remarks: '',
      status: null,
      skill: null
    })
    console.log(this.state);
  }
  
  render(){
    return (
      <div className="container mt-5">
        <form onSubmit={this.handleSubmit}>
          <div className="row text-center">
            <div className="form-group col-sm-4">
              <label htmlFor="datecomplete">Date Completed</label>
              <input type="text" id="datecomplete" className="form-control text-center" disabled value={this.Today()}/>
            </div>
            <div className="form-group col-sm-4">
              <label htmlFor="month">Month</label>
              <input type="text" id="month" className="form-control text-center" disabled value={this.Month()}/>
            </div>
            <div className="form-group col-sm-4">
              <label htmlFor="week">Week</label>
              <input type="text" id="week" className="form-control text-center" disabled value={this.Week()}/>
            </div>
          </div>
          <div className="row text-center">
            <div className="col-sm-4">
              <button type="button" className="btn btn-success" onClick={this.startTime}>Start Time</button>
              <input type="text" className="form-control mt-2 text-center" value={this.state.start} disabled/>
            </div>
            <div className="col-sm-4">
              <button type="button" className="btn btn-danger" onClick={this.endTime}>End Time</button>
              <input type="text" className="form-control mt-2 text-center" value={this.state.end} disabled/>
            </div>
            <div className="col-sm-4">
            <button type="button" className="btn btn-primary" disabled>Duration</button>
              <p>1:00:00</p>
            </div>
          </div>
          <div className="row text-center">
            <div className="form-group col-sm-3">
              <label htmlFor="theme">Theme</label>
              <input type="text" id="theme" className="form-control text-center" value={this.state.theme} onChange={this.handleChange}/>
            </div>
            <div className="form-group col-sm-3">
              <label htmlFor="ticketnumber">Ticket Number</label>
              <input type="text" id="ticketnumber" className="form-control text-center" value={this.state.ticketnumber} onChange={this.handleChange} required/>
            </div>
            <div className="form-group col-sm-6">
              <label htmlFor="website">Website</label>
              <input type="text" id="website" className="form-control text-center" value={this.state.website} onChange={this.handleChange} required/>
            </div>
          </div>
          <div className="row text-center">
            <div className="form-group col-sm-3">
              <label htmlFor="status">Status</label>
              <select id="status" className="form-control text-center" value={this.state.status} onChange={this.handleChange} required>
                <option disabled selected value> -- select an option -- </option>
                <option value="Complete">Complete</option>
                <option value="Returned">Returned</option>
              </select>
            </div>
            <div className="form-group col-sm-3">
              <label htmlFor="skill">Skill</label>
              <select id="skill" className="form-control text-center" value={this.state.skill} onChange={this.handleChange} required>
                <option disabled selected value> -- select an option -- </option>
                <option value="Migration">Migration</option>
                <option value="Regular Ticket">Regular Ticket</option>
              </select>
            </div>
            <div className="form-group col-sm-6">
              <label htmlFor="remarks">Remarks</label>
              <input type="text" id="remarks" className="form-control text-center" value={this.state.remarks} onChange={this.handleChange}/>
            </div>
          </div>
          <div className="row">
            <div className="col-sm-12 text-right">
              <button type="submit" className="btn btn-lg btn-success">Submit</button>
            </div>
          </div>
        </form>
      </div>
    )
  }
}

export default Form
